// import Axios from 'axios';
import React, { useState } from 'react'
import BtnRender from './BtnRender'
import axios from 'axios'
import loading from '../loading/Loading'

function ProductItem({product,setProduct, isAdmin,token,callback,
    setCallback
}) {
    const [loading, setLoading]= useState(false)

    const deleteProduct = async()=>{
      
        try {
            setLoading(true)
            const destroyImg = axios.post('/api/destroy', {public_id:product.images.public_id},{
                headers:{Authorization:token}
            })
            const deleteProduct = axios.delete(`/api/products/${product._id}`,{
                headers:{Authorization:token}
            })

            await destroyImg
            await deleteProduct
            setLoading(false)
            setCallback(!callback)
        } catch (err) {
            alert(err.response.data.msg);
        }
    }

      const handleCheck = (id) =>{
        const newProduct = [...product]
        newProduct.checked = !newProduct.checked
        // products.forEach(product => {
        //     if(product._id === id) product.checked = !product.checked
        // })
        
        // setProducts([...products])
        setProduct(newProduct)
    }


    if(loading) return <div className="product_card"><loading /></div>

    return(
        <div className="product_card">
             {
                isAdmin && <input type="checkbox" checked={product.checked}
                onChange={() => handleCheck(product._id)}
                 />
            }

           <img src={product.images.url} alt="" />

           <div className="product_box">
                <h2 title={product.title}>{product.title}</h2>
               <span>${product.price}</span>
              <p>{product.description}</p>
           </div>

           {/* <div className="row_btn"> */}
               {/* <a id="btn_buy"  href="#!">Buy</a>
               <a id="btn_view"  href={`/details/${product._id}`}>View</a> */}
               {/* <link id="btn_buy" to="#!">Buy</link>
               <link id="btn_view" to={`details/${product._id}`} >View</link> */}
           {/* </div> */}

           <BtnRender product={product} deleteProduct={deleteProduct} />
          
        </div>

        
    )

    // return (
    //     <div className="product_card">
    //         {
    //             isAdmin && <input type="checkbox" checked={product.checked}
    //             onChange={() => handleCheck(product._id)} />
    //         }
    //         <img src={product.images.url} alt="" />

    //         <div className="product_box">
    //             <h2 title={product.title}>{product.title}</h2>
    //             <span>${product.price}</span>
    //             <p>{product.description}</p>
    //         </div>

            
    //         <BtnRender product={product} deleteProduct={deleteProduct} />
    //     </div>
    // )
}

export default ProductItem
